import { Component, OnInit } from "@angular/core";

@Component({
  selector: "app-welcome",
  templateUrl: "./welcome.component.html",
  styleUrls: ["./welcome.component.css", "./welcome-component.responsivity.css"]
})

export class WelcomeComponent implements OnInit {

  constructor() { }

  ngOnInit(): void {
  }

}
<section id="welcome">
    <div class="layer_one">
        <div class="avatar-container">
            <img loading="lazy" class="illustration" src="../../../assets/template/welcome/gbastos-Illustration@2x.png" alt="Guilherme Borges Bastos"/>
            <app-welcome-background></app-welcome-background>
        </div>
        <app-welcome-dialog></app-welcome-dialog>
    </div>
</section>
